import './jogos.css'
import { Celula } from '../../models/bingo/celula';
import Select from 'react-select';
import { useState } from 'react';

function Jogos() {


  const [x3, setx3] = useState(false);
  const [x4, setx4] = useState(false);
  const [x5, setx5] = useState(false);
  const [classe, setClasse] = useState("x3");

  function countLines() {
    let texto = document.getElementById("inputBingo").value;
    var lines = texto.split("\n");
    var count = lines.length;
    console.log(count);

    if (count <= 9) {
      setx3(true)
      setx4(false)
      setx5(false)
      setClasse("x3");
    };
    if (count > 9 && count <= 16) {
      setx3(true)
      setx4(true)
      setx5(false)
      setClasse("x4");
    }
    if (count > 16 && count <= 25) {
      setx3(true)
      setx4(true)
      setx5(true)
      setClasse("x5");
    }
  }


  return (
    <div className="jogos">
      <div id='bingo' className={classe}>
        <Celula id="1" className="aparece"></Celula>
        <Celula id="2" className="aparece"></Celula>
        <Celula id="3" className="aparece"></Celula>
        <Celula id="4" className="aparece"></Celula>
        <Celula id="5" className="aparece"></Celula>
        <Celula id="6" className="aparece"></Celula>
        <Celula id="7" className="aparece"></Celula>
        <Celula id="8" className="aparece"></Celula>
        <Celula id="9" className="aparece"></Celula>

        <Celula id="10" className={x4 ? "aparece" : "escondido"}></Celula>
        <Celula id="11" className={x4 ? "aparece" : "escondido"}></Celula>
        <Celula id="12" className={x4 ? "aparece" : "escondido"}></Celula>
        <Celula id="13" className={x4 ? "aparece" : "escondido"}></Celula>
        <Celula id="14" className={x4 ? "aparece" : "escondido"}></Celula>
        <Celula id="15" className={x4 ? "aparece" : "escondido"}></Celula>
        <Celula id="16" className={x4 ? "aparece" : "escondido"}></Celula>

        <Celula id="17" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="18" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="19" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="20" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="21" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="22" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="23" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="24" className={x5 ? "aparece" : "escondido"}></Celula>
        <Celula id="25" className={x5 ? "aparece" : "escondido"}></Celula>
      </div>
      <div className='lista'>
        <h1>Jogos de Medo</h1>
        <textarea id='inputBingo' onChange={countLines} />
      </div>
    </div >
  );
}


export default Jogos;
