import './jogos.css'
import { Celula } from '../../models/bingo/celula';
import Select from 'react-select';
import { useState } from 'react';

function Jogos() {


  const [x3, setx3] = useState(false);
  const [x4, setx4] = useState(false);
  const [x5, setx5] = useState(false);

  function countLines() {
    let texto = document.getElementById("inputBingo").value;
    var lines = texto.split("\n");
    var count = lines.length;
    console.log(count);

    if (count <= 9) {
      const bingo = document.getElementById('bingo')
      bingo.className = "x3"
      setx3(true);
    };
  }


  return (
    <div className="jogos">
      <div id='bingo' className={x3 ? "x3" : x4 ? "x4" : x5 ? "x5" : ""}>
        <Celula className="x3cell" style={{visibility: x3 ? "visible" : "hidden"}}></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x4cell"></Celula>
        <Celula className="x4cell"></Celula>
        <Celula className="x4cell"></Celula>
        <Celula className="x4cell"></Celula>
        <Celula className="x4cell"></Celula>
        <Celula className="x4cell"></Celula>
        <Celula className="x4cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
      </div>
      <div className='lista'>
        <h1>Jogos de Medo</h1>
        <textarea id='inputBingo' onChange={countLines} />
      </div>
    </div>
  );
}


export default Jogos;
