import './jogos.css'
import { Celula } from '../../models/bingo/celula';
import Select from 'react-select';
import { useState } from 'react';

function Jogos() {


  const [x3, setx3] = useState("aparece");
  const [x4, setx4] = useState("escondido");
  const [x5, setx5] = useState("escondido");
  const [classe, setClasse] = useState("x3");

  function countLines() {
    let texto = document.getElementById("inputBingo").value;
    var lines = texto.split("\n");
    var count = lines.length;
    console.log(count);

    if (count <= 9) {
      setx3("aparece");
      setx4("escondido");
      setx5("escondido");
      setClasse("x3");
    };
    if (count > 9 && count <= 16){
      setx3("aparece");
      setx4("aparece");
      setx5("escondido")
      setClasse("x4");
    }
    if (count > 16 && count <= 25){
      setx3("aparece");
      setx4("aparece");
      setx5("aparece");
      setClasse("x5");
    }
  }


  return (
    <div className="jogos">
      <div id='bingo' className={classe}>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>
        <Celula className="x3cell"></Celula>

        <Celula className={x4 == true ? "escondido" : "aparece"}></Celula>
        <Celula className={x4 == true ? "escondido" : "aparece"}></Celula>
        <Celula className={x4 == true ? "escondido" : "aparece"}></Celula>
        <Celula className={x4 == true ? "escondido" : "aparece"}></Celula>
        <Celula className={x4 == true ? "escondido" : "aparece"}></Celula>
        <Celula className={x4 == true ? "escondido" : "aparece"}></Celula>
        <Celula className={x4 == true ? "escondido" : "aparece"}></Celula>

        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
        <Celula className="x5cell"></Celula>
      </div>
      <div className='lista'>
        <h1>Jogos de Medo</h1>
        <textarea id='inputBingo' onChange={countLines} />
      </div>
    </div>
  );
}


export default Jogos;
